import React, { Component } from 'react';
class TodoList extends Component {

    constructor(){
        super();

        this.state = {
            userInput : '',
            items:[]

        };
    }

    onChange(event){
        this.setState({
            userInput:event.target.value
        },() => console.log(this.state.userInput));
    } 

    render() {
        return(
            <div>
                <h1>Ma TodoList </h1>
                <form>
                    <input 
                        type = "text" 
                        value = { this.state.userInput } p
                        laceholder="Renseigner un item"
                        onChange = { ()=> this.onChange.bind(this) }>
                    </input>
                    <input 
                        type = "submit">
                    </input>
                </form>
            </div>
        );
    }

}
export default TodoList ;
