import React ,{ Component } from 'react';

class Home extends Component {
    constructor(){
        super();
        this.state = {
            count : 0
        }
    }

addOne(){
    this.setState({
        count: this.state.count + 1 
    });
}

removeOne(){
    this.setState({
        count: this.state.count - 1
    });
}

render() {
    return (
        <div>
            <h1> Bienvenue {this.props.name+" "+this.props.lol}</h1>
            <p>Mon conteur : {this.state.count}</p>
            <button onClick = { () => this.addOne() } >Ajouter 1</button>
            <button onClick = { this.removeOne.blind(this) }>Enlever</button>
        </div>


        )
}




}

export default Home;
